status.map:
function(doc) {
	if(doc.pvt_type == "phone_numbers"){
		for(campaign in doc.campaigns){
			var campaignType = doc.campaigns[campaign];
			var campaignTypeTranslators = doc.campaign_types[campaignType];
			for (areaCode in campaignTypeTranslators ) {
				var translatedAreaCode = campaignTypeTranslators [areaCode];
				var numbers = [];
				for(phonenumber in doc){
					if(phonenumber.indexOf('+' + translatedAreaCode) == 0 && doc[phonenumber]["additional"]["campaign"] == campaign ){
						numbers.push(phonenumber);
					}
				}
				emit(areaCode+"|"+campaign, numbers);
			}

		}

	}
}
